DROP TABLE IF EXISTS user;
DROP TABLE IF EXISTS cars;
DROP TABLE IF EXISTS user_role;
DROP TABLE IF EXISTS drive;
DROP TABLE IF EXISTS passenger_drive;
DROP TABLE IF EXISTS messages;


CREATE TABLE user
(
  id                 INT AUTO_INCREMENT PRIMARY KEY,
  name               VARCHAR(250) NOT NULL,
  phone_number       VARCHAR(250) NOT NULL,
  photo_url          VARCHAR(250),
  email              VARCHAR(250) NOT NULL,
  login              VARCHAR(250) NOT NULL,
  password           VARCHAR(250) NOT NULL,
  pref_communication VARCHAR(250) DEFAULT NULL,
  passenger_rating   DOUBLE,
  driver_rating      DOUBLE,
  points             INT,
  active             BOOLEAN
);

ALTER TABLE user
  ADD CONSTRAINT uniqueFirstName UNIQUE (login);

CREATE TABLE user_role
(
  user_id INT NOT NULL,
  roles   VARCHAR(10) check (roles in ('PASSENGER', 'DRIVER', 'ADMIN'))
);

CREATE TABLE drive
(
  id               INT AUTO_INCREMENT PRIMARY KEY,
  start_time       TIMESTAMP,
  end_time         TIMESTAMP,
  name             VARCHAR(250),
  free_place_count INT          NOT NULL,
  driver_id        VARCHAR(250) NOT NULL,
  start_point      VARCHAR(250),
  fin_point        VARCHAR(250),
  pathVO           CLOB,
  sum_of_km        DOUBLE,
  archive          BOOLEAN,
  route_url        VARCHAR(250),
  segments_coords  CLOB
);

CREATE TABLE cars
(
  id      INT AUTO_INCREMENT PRIMARY KEY,
  model   VARCHAR(100),
  color   VARCHAR(50),
  number  VARCHAR(50),
  user_id INT
);

CREATE TABLE passenger_drive
(
  passenger_id             INT          NOT NULL,
  drive_id                 INT          NOT NULL,
  start_point              VARCHAR(250) NOT NULL,
  number_of_km             DOUBLE,
  passenger_to_driver_mark INT DEFAULT NULL,
  driver_to_passenger_mark INT DEFAULT NULL
);

CREATE TABLE messages
(
  id        INT AUTO_INCREMENT PRIMARY KEY,
  drive_id  INT          NOT NULL,
  message   VARCHAR(250) NOT NULL,
  time      TIMESTAMP,
  user_id   INT,
  is_notify BOOLEAN
);

